import axios, { AxiosResponse } from 'axios';
import { LoginCredentials, User, Product, Order, ApiResponse, PaginationParams } from '../types';

// Day 2: Setting up proper axios API configuration
// Using JSONPlaceholder for now, but structured to easily switch to real backend
const API_BASE_URL = 'https://jsonplaceholder.typicode.com';

// Create axios instance with interceptors
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 second timeout
});

// Add token to requests if available
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('authToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Response interceptor for error handling
api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token expired or invalid
      localStorage.removeItem('authToken');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API - Day 2: Making it more realistic with axios
export const authAPI = {
  login: async (credentials: LoginCredentials): Promise<ApiResponse<{ user: User; token: string }>> => {
    try {
      // For demo purposes, we'll validate against mock data but use axios structure
      // In real app, this would be: const response = await api.post('/auth/login', credentials);

      // Simulate real API call delay
      await new Promise(resolve => setTimeout(resolve, 800));

      // Mock authentication check - replace with real API call
      if (credentials.email === 'demo@example.com' && credentials.password === 'password') {
        // Simulate fetching user data from JSONPlaceholder
        const userResponse = await api.get('/users/1');
        const userData = userResponse.data;

        const mockUser: User = {
          id: userData.id.toString(),
          email: credentials.email,
          name: userData.name,
          role: 'user'
        };

        const token = 'jwt-token-' + Date.now() + '-' + userData.id;

        return {
          success: true,
          data: { user: mockUser, token }
        };
      } else {
        throw new Error('Invalid email or password');
      }
    } catch (error: any) {
      console.error('Login error:', error);
      throw new Error(error.message || 'Login failed');
    }
  },

  logout: async (): Promise<void> => {
    try {
      // In real app: await api.post('/auth/logout');
      await new Promise(resolve => setTimeout(resolve, 300));
      localStorage.removeItem('authToken');
    } catch (error) {
      console.error('Logout error:', error);
      // Continue with logout even if API call fails
      localStorage.removeItem('authToken');
    }
  },

  verifyToken: async (token: string): Promise<ApiResponse<User>> => {
    try {
      // In real app: const response = await api.get('/auth/verify');
      await new Promise(resolve => setTimeout(resolve, 500));

      if (token.startsWith('jwt-token')) {
        // Simulate fetching user data
        const userResponse = await api.get('/users/1');
        const userData = userResponse.data;

        return {
          success: true,
          data: {
            id: userData.id.toString(),
            email: 'demo@example.com',
            name: userData.name,
            role: 'user'
          }
        };
      } else {
        throw new Error('Invalid token');
      }
    } catch (error: any) {
      console.error('Token verification error:', error);
      throw new Error('Token verification failed');
    }
  }
};

// Products API
export const productsAPI = {
  getProducts: async (params?: PaginationParams): Promise<ApiResponse<{ products: Product[]; totalPages: number }>> => {
    // Mock products data
    const mockProducts: Product[] = [
      {
        id: '1',
        name: 'Wireless Headphones',
        description: 'High-quality wireless headphones with noise cancellation',
        price: 199.99,
        category: 'electronics',
        imageUrl: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
        inStock: true,
        rating: 4.5,
        reviews: 128
      },
      {
