import axios from 'axios';
import { LoginCredentials, User, Product, Order, ApiResponse, PaginationParams } from '../types';

// Mock API base URL - replace with your actual API endpoint
const API_BASE_URL = 'https://jsonplaceholder.typicode.com';

// Create axios instance with interceptors
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests if available
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('authToken');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Auth API
export const authAPI = {
  login: async (credentials: LoginCredentials): Promise<ApiResponse<{ user: User; token: string }>> => {
    // Mock login - replace with actual API call
    await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API delay

    if (credentials.email === 'demo@example.com' && credentials.password === 'password') {
      const mockUser: User = {
        id: '1',
        email: credentials.email,
        name: 'Demo User',
        role: 'user'
      };
      const token = 'mock-jwt-token-' + Date.now();

      return {
        success: true,
        data: { user: mockUser, token }
      };
    }

    throw new Error('Invalid credentials');
  },

  logout: async (): Promise<void> => {
    // Mock logout
    await new Promise(resolve => setTimeout(resolve, 500));
  },

  verifyToken: async (token: string): Promise<ApiResponse<User>> => {
    // Mock token verification
    await new Promise(resolve => setTimeout(resolve, 500));

    if (token.startsWith('mock-jwt-token')) {
      return {
        success: true,
        data: {
          id: '1',
          email: 'demo@example.com',
          name: 'Demo User',
          role: 'user'
        }
      };
    }

    throw new Error('Invalid token');
  }
};

// Products API
export const productsAPI = {
  getProducts: async (params?: PaginationParams): Promise<ApiResponse<{ products: Product[]; totalPages: number }>> => {
    // Mock products data
    const mockProducts: Product[] = [
      {
        id: '1',
        name: 'Wireless Headphones',
        description: 'High-quality wireless headphones with noise cancellation',
        price: 199.99,
        category: 'electronics',
        imageUrl: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
        inStock: true,
        rating: 4.5,
        reviews: 128
      },
      {
        id: '2',
        name: 'Smartphone',
        description: 'Latest model smartphone with advanced features',
        price: 799.99,
        category: 'electronics',
        imageUrl: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=400',
        inStock: true,
        rating: 4.8,
        reviews: 256
      },
      {
        id: '3',
        name: 'Running Shoes',
        description: 'Comfortable running shoes for daily workouts',
        price: 129.99,
        category: 'sports',
        imageUrl: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=400',
        inStock: false,
        rating: 4.2,
        reviews: 89
      },
      {
        id: '4',
        name: 'Coffee Maker',
        description: 'Automatic coffee maker with programmable settings',
        price: 89.99,
        category: 'home',
        imageUrl: 'https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400',
        inStock: true,
        rating: 4.0,
        reviews: 45
      },
      {
        id: '5',
        name: 'Laptop Backpack',
        description: 'Durable laptop backpack with multiple compartments',
        price: 59.99,
        category: 'accessories',
        imageUrl: 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400',
        inStock: true,
        rating: 4.3,
        reviews: 67
      },
      {
        id: '6',
        name: 'Desk Lamp',
        description: 'LED desk lamp with adjustable brightness',
        price: 39.99,
        category: 'home',
        imageUrl: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400',
        inStock: true,
        rating: 4.1,
        reviews: 23
      }
    ];

    await new Promise(resolve => setTimeout(resolve, 800)); // Simulate API delay

    let filteredProducts = [...mockProducts];

    // Apply filters
    if (params?.category) {
      filteredProducts = filteredProducts.filter(p => p.category === params.category);
    }
    if (params?.minPrice) {
      filteredProducts = filteredProducts.filter(p => p.price >= params.minPrice!);
    }
    if (params?.maxPrice) {
      filteredProducts = filteredProducts.filter(p => p.price <= params.maxPrice!);
    }
    if (params?.inStock !== undefined) {
      filteredProducts = filteredProducts.filter(p => p.inStock === params.inStock);
    }
    if (params?.minRating) {
      filteredProducts = filteredProducts.filter(p => p.rating >= params.minRating!);
    }

    // Apply sorting
    if (params?.sortBy) {
      filteredProducts.sort((a, b) => {
        let aVal: any, bVal: any;

        switch (params.sortBy) {
          case 'name':
            aVal = a.name;
            bVal = b.name;
            break;
          case 'price':
            aVal = a.price;
            bVal = b.price;
            break;
          case 'rating':
            aVal = a.rating;
            bVal = b.rating;
            break;
          default:
            return 0;
        }

        if (params.sortOrder === 'desc') {
          return bVal > aVal ? 1 : -1;
        }
        return aVal > bVal ? 1 : -1;
      });
    }

    // Apply pagination
    const limit = params?.limit || 6;
    const page = params?.page || 1;
    const startIndex = (page - 1) * limit;
    const paginatedProducts = filteredProducts.slice(startIndex, startIndex + limit);
    const totalPages = Math.ceil(filteredProducts.length / limit);

    return {
      success: true,
      data: {
        products: paginatedProducts,
        totalPages
      }
    };
  },

  getProductById: async (id: string): Promise<ApiResponse<Product>> => {
    const { data } = await productsAPI.getProducts();
    const product = data.products.find(p => p.id === id);

    if (!product) {
      throw new Error('Product not found');
    }

    return {
      success: true,
      data: product
    };
  }
};

// Orders API
export const ordersAPI = {
  createOrder: async (items: any[], totalAmount: number): Promise<ApiResponse<Order>> => {
